    nouvelle_bonification_indiciaire = individu('nouvelle_bonification_indiciaire', period)
    categorie_salarie = individu('categorie_salarie', period)

    public = (
        (categorie_salarie == TypesCategorieSalarie.public_titulaire_etat)
        + (categorie_salarie == TypesCategorieSalarie.public_titulaire_militaire)
        + (categorie_salarie == TypesCategorieSalarie.public_titulaire_territoriale)
        + (categorie_salarie == TypesCategorieSalarie.public_titulaire_hospitaliere)
        )

    return (
        public * (
            traitement_indiciaire_brut + nouvelle_bonification_indiciaire
            )
        )

def formula(individu, period, parameters):
    age = individu('age', period)
    smic = parameters(period).cotsoc.gen.smic_h_b * 52 * 35 / 12
    professionnalisation = individu('professionnalisation', period)
    qualifie = individu('qualifie', period)
    salaire_en_smic = [
        dict(
            part_de_smic_by_qualification = {
                'non_qualifie': .55,
                'qualifie': .65
                },
            age_min = 16,
            age_max = 21,
            ),
        dict(
            part_de_smic_by_qualification = {
                1: .70,
                },
            age_min = 21,
            age_max = 25,
            ),
        dict(
            part_de_smic_by_qualification = {
                1: 1.0,
                },
            age_min = 26,
            age_max = 99
            )
        ]

    taux_smic = age * 0.0
    for age_interval in salaire_en_smic:
        age_condition = (age_interval['age_min'] <= age) * (age <= age_interval['age_max'])
        taux_smic[age_condition] = sum([
            (qualifie[age_condition] == qualification) * part_de_smic
            for qualification, part_de_smic in age_interval['part_de_smic_by_qualification'].items()
            ])
    return taux_smic * smic * professionnalisation

